import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [question, setQuestion] = useState("");
  const [response, setResponse] = useState("");
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [imageBlobUrl, setImageBlobUrl] = useState(null);

  // Функция для загрузки изображения как Blob URL
  const fetchImage = async () => {
    try {
      const response = await fetch("http://127.0.0.1:8000/images/page_128.png");
      if (!response.ok) throw new Error("Ошибка загрузки изображения");
      const blob = await response.blob();
      const url = URL.createObjectURL(blob);
      setImageBlobUrl(url);
    } catch (error) {
      console.error("Ошибка:", error);
    }
  };

  useEffect(() => {
    fetchImage();
  }, []);

  // Обработка отправки вопроса
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setResponse("");
    setImages([]);

    try {
      const res = await fetch("http://127.0.0.1:8000/ask", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ question }),
      });

      if (res.ok) {
        const data = await res.json();

        // Обработка ответа
        setResponse(data.answer || "Ответ не найден");

        // Загружаем изображения
        if (Array.isArray(data.images)) {
          const loadedImages = await Promise.all(
            data.images.map(async (image) => {
              const blobUrl = await fetchImageBlob(image.url);
              return {
                ...image,
                blobUrl, // Добавляем blob URL для отображения
              };
            })
          );
          setImages(loadedImages);
        } else {
          console.warn("Нет изображений в ответе");
        }
      } else {
        setResponse("Ошибка сервера");
      }
    } catch (err) {
      console.error("Ошибка сети:", err);
      setResponse("Ошибка сети");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>NorNikel Finder</h1>

      {/* Форма для ввода вопроса */}
      <form onSubmit={handleSubmit}>
        <textarea
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          rows={5}
          placeholder="Введите ваш вопрос..."
          required
        ></textarea>
        <button type="submit" disabled={loading}>
          {loading ? "Загрузка..." : "Отправить"}
        </button>
      </form>

      {/* Ответ от сервера */}
      <div className="response">
        <h2>Ответ:</h2>
        <p>{response || "Ответ появится здесь..."}</p>
      </div>

      {/* Отображение изображений */}
      <div className="images">
        <h2>Страницы с которых бралась информация:</h2>
        <div className="image-grid">
          {images.map((image, index) => (
            <div key={index} className="image-item">
              {image.blobUrl ? (
                <img src={imageBlobUrl} alt={`Image ${index + 1}`} />
              ) : (
                <p>Ошибка загрузки изображения</p>
              )}
              <p>
                <strong>Файл:</strong> {image.filename} <br />
                <strong>Страница:</strong> {image.page}
              </p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
