import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [file, setFile] = useState(null); // Для загрузки файла

  const sendQuestion = async () => {
    if (!question.trim()) return;
    setLoading(true);

    try {
      const formData = new FormData();
      formData.append('question', question);
      if (file) formData.append('file', file); // Добавляем файл, если выбран

      const response = await axios.post('http://127.0.0.1:8000/ask', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setAnswer(response.data.answer);
      setImages(response.data.images);
    } catch (error) {
      console.error('Ошибка при отправке вопроса:', error);
      setAnswer('Произошла ошибка. Попробуйте позже.');
    } finally {
      setLoading(false);
    }
  };


  return (
    <div className="App">
      <header className="App-header">
        <h1>NorNikel Чат</h1>
      </header>
      <main>
        <div className="chat-container">
          <div className="input-area">
            <textarea
              placeholder="Введите ваш вопрос..."
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
            />
            <button onClick={sendQuestion} disabled={loading}>
              {loading ? 'Отправка...' : 'Отправить'}
            </button>
          </div>



          <div className="response-area">
            <h2>Ответ:</h2>
            <p>{answer}</p>
            <h3>Изображения:</h3>
            <div className="images-grid">
              {images.map((img, index) => (
                <div key={index} className="image-item">
                  <a href={`http://127.0.0.1:8000${img.url}`} target="_blank" rel="noopener noreferrer">
                    <img src={`http://127.0.0.1:8000${img.url}`} alt={`Страница ${img.page}`} />
                  </a>
                  <p>{img.filename} - Страница {img.page}</p>
                </div>
              ))}
            </div>
          </div>
          {/* Загрузка файла */}
          <div className="file-upload">
            <h2>Добавление файлов в базу знаний:</h2>
            <input
              type="file"
              onChange={handleFileChange}
              accept=".txt, .pdf"
            />
            {file && <p>Выбран файл: {file.name}</p>} {/* Отображаем имя выбранного файла */}
          </div>
          <button>
              Отправить
            </button>
        </div>
      </main>
    </div>
  );
}

export default App;
